const initstate = {}

const authreducer = (state=initstate, action)=>{
    switch(action.type){
        case 'LOGIN_ERROR':
          console.log('login error');
          return {
            ...state,
            authError: 'Login failed'
          }
        case 'LOGIN_SUCCESS':
          console.log('login success');
          return {
            authError: null
          }
        default:
          return state
      }
}

export default authreducer
